import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { currentUser } from '../lib/currentUser';
import DeleteItem from '../Components/DeleteItem.jsx'; // Import the DeleteItem component

function ProfilePage() {
  const [userItems, setUserItems] = useState([]);
  const userToken = localStorage.getItem('access_token'); 
  const userId = currentUser(); 

  useEffect(() => {
    // Fetch user's items
    axios.get(`${process.env.REACT_APP_BACKEND_URL}/profile/${userId}/items/`, {
      headers: {
        Authorization: `Bearer ${userToken}`
      }
    })
    .then(response => {
      setUserItems(response.data);
    })
    .catch(error => {
      console.error('Error fetching user items:', error);
    });
  }, [userToken, userId]);

  const handleDeleteSuccess = (deletedItemId) => {
    setUserItems(prevItems => prevItems.filter(item => item.id !== deletedItemId));
  };

  return (
    <div>
      <h1>Your Items</h1>
      <div className="card-container">
        {userItems.map(item => (
          <div className="item-card" key={item.id}>
            <h2>{item.item_title}</h2>
            <p>{item.item_description}</p>
            <DeleteItem itemId={item.id} onSuccess={handleDeleteSuccess} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfilePage;
