import axios from "axios";
import { useState } from "react";

// Define the SignupForm function.
export const SignupForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Create the submit method.
  const submit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: username,
      password: password,
    };

    try {
      // Create the POST request
      const { data } = await axios.post(`http://localhost:8000/login/`, newUser, {
        headers: { "Content-Type": "application/json" },
      });

      // Assuming successful signup, you might handle the response accordingly
      console.log("User signed up successfully:", data);

      // Optionally, you might want to redirect the user to a login page after signup
      // window.location.href = "/login";
    } catch (error) {
      console.error("Error signing up:", error);
      // Handle errors here
    }
  };

  return (
    <div className="Auth-form-container">
      <form className="Auth-form" onSubmit={submit}>
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Sign Up</h3>
          <div className="form-group mt-3">
            <label>Username</label>
            <input
              className="form-control mt-1"
              placeholder="Enter Username"
              name="username"
              type="text"
              value={username}
              required
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              name="password"
              type="password"
              className="form-control mt-1"
              placeholder="Enter password"
              value={password}
              required
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="d-grid gap-2 mt-3">
            <button type="submit" className="btn btn-primary">
              Sign Up
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SignupForm;
