import React, { useState } from 'react';

function SwapForm({ onSubmit }) {
  const [itemTitle, setItemTitle] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [offerAccepted, setOfferAccepted] = useState(false);
  const [user, setUser] = useState(null); // Assuming user data needs to be fetched
  const [item, setItem] = useState(null); // Assuming item data needs to be fetched

  const handleTitleChange = (e) => {
    setItemTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setItemDescription(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ item_title: itemTitle, item_description: itemDescription, offer_accepted: offerAccepted, user: user.id, item: item.id });
    setItemTitle('');
    setItemDescription('');
    setOfferAccepted(false);
    setUser(null);
    setItem(null);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h4>Make your offer:</h4>
        <label>
          Item Title:
          <input type="text" value={itemTitle} onChange={handleTitleChange} />
        </label>
        <br />
        <label>
          Item Description:
          <textarea value={itemDescription} onChange={handleDescriptionChange} />
        </label>
        <br />
        <label>
          Offer Accepted:
          <input type="checkbox" checked={offerAccepted} onChange={() => setOfferAccepted(!offerAccepted)} />
        </label>
        <input type="hidden" value={user.id} />
        <input type="hidden" value={item.id} />
        <button type="submit">Submit Offer</button>
      </form>
    </div>
  );
}

export default SwapForm;
