import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie'; // Import js-cookie library

function ItemEdit({ itemId, userToken }) {
    const [item, setItem] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch item details
        axios.get(`/items/${itemId}/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            setItem(response.data);
            setLoading(false);
        })
        .catch(error => {
            setError('Error fetching item details');
            setLoading(false);
        });
    }, [itemId, userToken]);

    const getCsrfToken = () => {
        const csrfTokenCookie = document.cookie.split(';')
            .map(c => c.trim())
            .find(c => c.startsWith('csrftoken='));
        if (!csrfTokenCookie) {
            console.error('CSRF token cookie not found');
            return null;
        }
        const csrfToken = csrfTokenCookie.split('=')[1];
        console.log('Retrieved CSRF token:', csrfToken);
        return csrfToken;
    };

    // Function to create Axios instance with CSRF token header
    const axiosWithCsrf = axios.create({
        baseURL: process.env.REACT_APP_BACKEND_URL,
        headers: {
            'X-CSRFToken': getCsrfToken(), // Set the CSRF token from cookies
            'Authorization': `Bearer ${userToken}`
        },
    });

    // Log the headers before making the request
    console.log('Request headers:', axiosWithCsrf.defaults.headers);

    // Send a PUT request to update the item data using axiosWithCsrf instance
    axiosWithCsrf.put(
        `/items/${itemId}/`, 
        item
    )
    .then(response => {
        console.log('Item updated successfully:', response.data);
        // Optionally, update the local state with the updated item data
        // setItem(response.data);
        // Optionally, show a success message to the user
    })
    .catch(error => {
        console.error('Error updating item:', error);
        // Optionally, show an error message to the user
    });

    const handleFormSubmit = (event) => {
        event.preventDefault();

        // Send a PUT request to update the item data using axiosWithCsrf instance
        axiosWithCsrf.put(
            `/items/${itemId}/`, 
            item
        )
        .then(response => {
            console.log('Item updated successfully:', response.data);
            // Optionally, update the local state with the updated item data
            // setItem(response.data);
            // Optionally, show a success message to the user
        })
        .catch(error => {
            console.error('Error updating item:', error);
            // Optionally, show an error message to the user
        });
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setItem(prevItem => ({
            ...prevItem,
            [name]: value
        }));
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Edit Item</h2>
            <form onSubmit={handleFormSubmit}>
                <label>
                    Title:
                    <input
                        type="text"
                        name="item_title"
                        value={item?.item_title}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Description:
                    <textarea
                        name="item_description"
                        value={item?.item_description}
                        onChange={handleInputChange}
                    ></textarea>
                </label>
                <br />
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default ItemEdit;
