import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ItemEdit({ itemId, userToken }) {
    const [item, setItem] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [imageFile, setImageFile] = useState(null);

    useEffect(() => { 
        fetchItemDetails();
    }, []);

    const fetchItemDetails = () => {
        setLoading(true);
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            setItem(response.data);
            setLoading(false);
        })
        .catch(error => {
            setError('Error fetching item details');
            setLoading(false);
        });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('item_title', item.item_title);
        formDataToSend.append('item_description', item.item_description);
        formDataToSend.append('image', imageFile); // Append image file

        axios.post(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/edit`, formDataToSend, {
            headers: {
                'Content-Type': 'multipart/form-data',
                Authorization: `Bearer ${userToken}`
            }
        })
            .then(response => {
                console.log('Item updated successfully:', response.data);
                // Redirect or perform any other action after successful update
            })
            .catch(error => {
                console.error('Error:', error.response.data);
            });
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setItem(prevItem => ({
            ...prevItem,
            [name]: value
        }));
    };

    const handleImageChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="Auth-form">
            <h2 className="Auth-form-title">Edit Item</h2>
            <form onSubmit={handleFormSubmit}>
                <div className="form-group">
                    <label className="form-label">
                        Title:
                        <input
                            type="text"
                            name="item_title"
                            value={item?.item_title || ''}
                            onChange={handleInputChange}
                            className="form-control"
                        />
                    </label>
                </div>
                <div className="form-group">
                    <label className="form-label">
                        Description:
                        <textarea
                            name="item_description"
                            value={item?.item_description || ''}
                            onChange={handleInputChange}
                            className="form-control"
                        ></textarea>
                    </label>
                </div>
                <div className="form-group">
                    <label className="form-label">Image:</label>
                    <input
                        type="file"
                        name="image"
                        onChange={handleImageChange}
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <button className="btn btn-primary" type="submit">Save Changes</button>
                </div>
            </form>
        </div>
    );
}

export default ItemEdit;
