import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import { Nav, Navbar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { currentUser } from '../lib/currentUser.js'; // Import the currentUser function
import '../App/App.css';

export default function Menu() {
  const [isAuth, setIsAuth] = useState(false);
  const [userId, setUserId] = useState(null); // State to store user ID
  const history = useHistory(); // Access the history object

  useEffect(() => {
    // Check if user is authenticated
    if (localStorage.getItem('access_token') !== null) {
      setIsAuth(true);
      const id = currentUser(); // Get current user ID
      setUserId(id); // Set user ID state
    } else {
      setIsAuth(false);
      setUserId(null);
    }
  }, [isAuth]); // Trigger effect when isAuth changes

  const handleLogout = () => {
    // Clear localStorage and redirect to login page
    localStorage.removeItem('access_token');
    setIsAuth(false);
    setUserId(null);
    history.push('/login'); // Redirect to login page
  };

  return (
    <nav className="custom-navbar">
      <Navbar expand="lg">
        <Navbar.Brand style={{ color: '#6f42c1' }}>Sustainable Swap Shop</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link href="/">Home</Nav.Link>
            {isAuth ? (
              <>
                <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
                <Nav.Link href="/items/create">Add Item</Nav.Link>
                {/* Render profile link only if userId is available */}
                {userId && (
                  <Link to={`/profile/${userId}/items/`} className="nav-link">
                    Profile
                  </Link>
                )}
              </>
            ) : (
              <Nav.Link href="/login">Login/Signup</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </nav>
  );
}
