import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { currentUser } from '../lib/currentUser';

function ProfilePage() {
    const [userItems, setUserItems] = useState([]);
    const [userName, setUserName] = useState('');
    const userToken = localStorage.getItem('access_token'); // Changed to access_token
    const userId = currentUser(); // Get user ID from currentUser function

    useEffect(() => {
        // Fetch user's items and name
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/profile/${userId}/items/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            setUserItems(response.data);
            setUserName(response.data.user_name); // Assuming user name is included in the response
        })
        .catch(error => {
            console.error('Error fetching user items:', error);
            // Handle errors here (e.g., show error message)
        });
    }, [userToken, userId]);

    return (
        <div>
            <h1>{userName}'s Items</h1>
            <div className="card-container">
                {userItems.map(item => (
                    <div className="item-card" key={item.id}>
                        <h2>{item.item_title}</h2>
                        <p>{item.item_description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProfilePage;
