import React, { useState, useEffect } from 'react';
import { currentUser } from '../lib/currentUser.js'; // Import the currentUser function

function SwapForm({ onSubmit, ownerId }) {
  const [itemTitle, setItemTitle] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [offerAccepted, setOfferAccepted] = useState(false);
  const [userId, setUserId] = useState(null); // Store the user ID

  const currentUserId = currentUser()
  const initialState = { 
    item_title: '', 
    item_description: '', 
    offer_accepted: false, 
    user: currentUserId
     }
  const [item, setItem] = useState (initialState)

  const handleTitleChange = (e) => {
    setItemTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setItemDescription(e.target.value);
  };

  const handleChange = (e) => {
    setItem({ ...item, [e.target.name]: value})
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setItemTitle('');
    setItemDescription('');
    setOfferAccepted(false);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h4>Make your offer:</h4>
        <label>
          Item Title:
          <input type="text" value={item.item_title} onChange={handleTitleChange} />
        </label>
        <br />
        <label>
          Item Description:
          <textarea value={itemDescription} onChange={handleDescriptionChange} />
        </label>
        {ownerId === userId && ( 
          <>
            <br />
            <label>
              Offer Accepted:
              <input type="checkbox" checked={offerAccepted} onChange={() => setOfferAccepted(!offerAccepted)} />
            </label>
          </>
        )}
        <input type="hidden" value={currentUserId} /> {/* Provide the user ID as a hidden input */}
        <button type="submit">Submit Offer</button>
      </form>
    </div>
  );
}

export default SwapForm;
