import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { currentUser } from '../lib/currentUser';
import ItemEdit from '../Components/ItemEdit.jsx';

function ProfilePage() {
    const [userItems, setUserItems] = useState([]);
    const userToken = localStorage.getItem('access_token');
    const userId = currentUser();
    const [editingItemId, setEditingItemId] = useState(null);

    useEffect(() => {
        refreshUserItems(userId, userToken);
    }, [userToken, userId]);

    const refreshUserItems = (userId, userToken) => {
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/profile/${userId}/items/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            if (Array.isArray(response.data)) {
                Promise.all(response.data.map(item =>
                    axios.get(`${process.env.REACT_APP_BACKEND_URL}/items/${item.id}/swaps/`, {
                        headers: {
                            Authorization: `Bearer ${userToken}`
                        }
                    }).then(swapsResponse => {
                        item.swaps = swapsResponse.data;
                        return item;
                    })
                )).then(itemsWithSwaps => {
                    setUserItems(itemsWithSwaps);
                }).catch(error => {
                    console.error('Error fetching swaps for items:', error);
                });
            } else {
                console.error('Invalid response format for user items:', response.data);
            }
        })
        .catch(error => {
            console.error('Error fetching user items:', error);
        });
    };

    const handleDeleteItem = (itemId) => {
        axios.delete(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/delete/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            setUserItems(prevItems => prevItems.filter(item => item.id !== itemId));
        })
        .catch(error => {
            console.error('Error deleting item:', error);
        });
    };

    const handleDeleteSwap = (swapId) => {
        axios.delete(`${process.env.REACT_APP_BACKEND_URL}/swaps/${swapId}/delete/`, {
            headers: {
                Authorization: `Bearer ${userToken}`
            }
        })
        .then(response => {
            setUserItems(prevItems =>
                prevItems.map(item => {
                    item.swaps = item.swaps.filter(swap => swap.id !== swapId);
                    return item;
                })
            );
        })
        .catch(error => {
            console.error('Error deleting swap:', error);
        });
    };

    return (
        <div>
            <h1>Your Items</h1>
            <div className="card-container">
                {userItems.map(item => (
                    <div className="item-card" key={item.id}>
                        {item && (
                            <>
                                <Link to={`/items/${item.id}`}>
                                    <h2>{item.item_title}</h2>
                                </Link>
                                <p>{item.item_description}</p>
                                <h4>Swaps:</h4>
                                <div>
                                    {item.swaps && item.swaps.length > 0 ? (
                                        item.swaps.map(swap => (
                                            <div key={swap.id}>
                                                <p>{swap.item_title}, {swap.item_description}</p>
                                                {userId === item.user && (
                                                    <button onClick={() => handleDeleteSwap(swap.id)}>X</button>
                                                )}
                                            </div>
                                        ))
                                    ) : (
                                        <p>No swaps yet</p>
                                    )}
                                </div>
                                <button className="btn btn-primary" onClick={() => handleDeleteItem(item.id)}>Delete Item</button>
                                <button className="btn btn-primary" onClick={() => setEditingItemId(item.id)}>Edit Item</button>
                            </>
                        )}
                    </div>
                ))}
            </div>
            {editingItemId && (
                <ItemEdit
                    itemId={editingItemId}
                    userToken={userToken}
                />
            )}
        </div>
    );
}

export default ProfilePage;
