import React, { useState } from 'react';
import axios from 'axios';

function ItemCreate({ item_id }) { // Accept 'item_id' as a prop
    const [formData, setFormData] = useState({
        item_title: '',
        item_description: '',
        listing_active: true,
        user: 2,
        photo: null
    });

    const handleChange = (e) => {
        if (e.target.name === 'photo') {
            setFormData({
                ...formData,
                photo: e.target.files[0]
            });
        } else {
            setFormData({
                ...formData,
                [e.target.name]: e.target.value
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formDataWithPhoto = new FormData();
        formDataWithPhoto.append('item_title', formData.item_title);
        formDataWithPhoto.append('item_description', formData.item_description);
        formDataWithPhoto.append('listing_active', formData.listing_active);
        formDataWithPhoto.append('user', formData.user);
        formDataWithPhoto.append('photo', formData.photo);

        axios.post(`/add-photo/${item_id}/`, formDataWithPhoto, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error('Error:', error.response.data);
            });
    };

    return (
        <div className="Auth-form">
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="item_title" className="form-label">Title:</label>
                    <input type="text" id="item_title" name="item_title" value={formData.item_title} onChange={handleChange} className="form-control" />
                </div>
                <div className="mb-3">
                    <label htmlFor="item_description" className="form-label">Description:</label>
                    <textarea id="item_description" name="item_description" value={formData.item_description} onChange={handleChange} className="form-control"></textarea>
                </div>
                <div className="mb-3">
                    <label htmlFor="photo" className="form-label">Photo:</label>
                    <input type="file" id="photo" name="photo" onChange={handleChange} className="form-control" />
                </div>
                <div className="d-grid gap-2 mt-3">
                    <button type="submit" className="btn btn-primary">Submit</button>
                </div>
            </form>
        </div>
    );
}

export default ItemCreate;
