import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../App/App.css';
import SwapForm from './SwapForm';

function ItemViewPage({ currentUser }) {
  const { itemId } = useParams();
  console.log("Item ID:", itemId);

  const [item, setItem] = useState(null);
  const [swaps, setSwaps] = useState([]);

  const handleFormSubmit = (formData) => {
    axios.post(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/create-swap/`, formData)
      .then(response => {
        console.log("Offer submitted:", response.data);
        const newSwap = response.data;
        setSwaps(prevSwaps => [...prevSwaps, newSwap]);
      })
      .catch(error => {
        console.error('Error submitting offer:', error);
      });
  };

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        const itemResponse = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/`);
        setItem(itemResponse.data);

        const swapsResponse = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/items/${itemId}/swaps/`);
        setSwaps(swapsResponse.data);
      } catch (error) {
        console.error('Error fetching item details:', error);
      }
    };

    fetchItemDetails();
  }, [itemId]);

  const s3BaseUrl = "https://sdg-ga-seb77.s3.amazonaws.com";

  console.log("Current User:", currentUser);
  console.log("Item Owner:", item?.item_user);

  return (
    <div className="centered-item-card-container">
      {item ? (
        <div className="item-card enlarged">
          <h1>{item.item_title}</h1>
          <p>{item.item_description}</p>
          {item.image && (
            <img
              src={`${s3BaseUrl}/${item.image_url}/`}
              alt={item.item_title}
            />
          )}
          <h4>Swaps:</h4>
          <div>
            {swaps.length > 0 ? (
              swaps.map(swap => (
                <p key={swap.id}>{swap.item_title}, {swap.item_description}</p>
              ))
            ) : (
              <p>No offers yet</p>
            )}
          </div>
          {currentUser !== item?.item_user && <SwapForm onSubmit={handleFormSubmit} ownerId={item.item_user} itemId={item.item_id} />}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default ItemViewPage;
